
def cmd(api, message, owner_id):
    if message['from_id'] == owner_id:
        api.messages.send(
            peer_id=message['peer_id'],
            random_id=0,
            message=f"üöë –î–æ—Å—Ç—É–ø–Ω—ã–µ –∫–æ–º–∞–Ω–¥—ã >>\n/au, /audio -- –∂–º—ã—Ö –≥–æ–ª—Å–æ–≤–æ–≥–æ —Å–æ–æ–±—â–µ–Ω–∏—è\n/d, /dist -- –∂–º—ã—Ö —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏\n/ma, /music_audio -- –ø–µ—Ä–≤–µ—Å—Ç–∏ –ø–µ—Å–Ω—é –≤ –≥–æ–ª–æ—Å–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ\n/n, /negative -- –ø–µ—Ä–≤–µ—Å—Ç–∏ —Ñ–æ—Ç–æ–≥–∞—Ä—Ñ–∏—é –≤ –Ω–µ–≥–∞—Ç–∏–≤\n/t, /text -- –¥–æ–±–∞–≤–∏—Ç—å —Ç–µ–∫—Å—Ç –∫ —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏\n/tc, /tester_check -- –ø—Ä–æ–≤–µ—Ä–∏—Ç—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –Ω–∞ —Ç–µ—Å—Ç–µ—Ä–∞\n/userid, /uid -- –ø–æ–ª—É—á–∏—Ç—å ID –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è\n\n‚ùóÔ∏è –ö–æ–º–∞–Ω–¥—ã –≤–ª–∞–¥–µ–ª—å—Ü–∞:\n/ban -- –∑–∞–±–ª–æ–∫–∏—Ä–æ–≤–∞—Ç—å –¥–æ—Å—Ç—É–ø –∫ –±–æ—Ç—É –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—é\n/ban_chat -- –∑–∞–±–ª–æ–∫–∏—Ä–æ–≤–∞—Ç—å —á–∞—Ç (–±–æ—Ç –Ω–µ –±—É–¥–µ—Ç –≤ –Ω–µ–º —Ä–∞–±–æ—Ç–∞—Ç—å)\n/ignore -- –¥–æ–±–∞–≤–∏—Ç—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –≤ –∏–≥–Ω–æ—Ä (–µ–≥–æ —Å–æ–æ–±—â–µ–Ω–∏—è –±—É–¥—É—Ç —É–¥–∞–ª—è—Ç—Å—è –¥–ª—è –≤–∞—Å)\n/copy -- —Å–∫–æ–ø–∏—Ä–æ–≤–∞—Ç—å –≥–æ–ª–æ—Å–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ\n/del -- —É–¥–∞–ª–∏—Ç—å —Å–æ–æ–±—â–µ–Ω–∏—è\n/i, /–∏ -- –æ—Ç–ø—Ä–∞–≤–∏—Ç—å –∏—Å—á–µ–∑–∞—é—â–µ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ\n/repeat -- —Å–ø–∞–º —Å–æ–æ–±—â–µ–Ω–∏—è–º–∏\n/unban -- —Ä–∞–∑–±–ª–æ–∫–∏—Ä–æ–≤–∞—Ç—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è\n/unban_chat -- —Ä–∞–∑–±–ª–æ–∫–∏—Ä–æ–≤–∞—Ç—å —á–∞—Ç\n/unignore -- —É–¥–∞–ª–∏—Ç—å –∏–∑ –∏–≥–Ω–æ—Ä–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è\n\nüê∫ –†–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫: [id498281739|—Ç—ã–∫]",
            reply_to=message['id']
        )
    else:
        api.messages.send(
            peer_id=message['peer_id'],
            random_id=0,
            message=f"üöë –î–æ—Å—Ç—É–ø–Ω—ã–µ –∫–æ–º–∞–Ω–¥—ã >>\n/au, /audio -- –∂–º—ã—Ö –≥–æ–ª—Å–æ–≤–æ–≥–æ —Å–æ–æ–±—â–µ–Ω–∏—è\n/d, /dist -- –∂–º—ã—Ö —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏\n/ma, /music_audio -- –ø–µ—Ä–≤–µ—Å—Ç–∏ –ø–µ—Å–Ω—é –≤ –≥–æ–ª–æ—Å–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ\n/n, /negative -- –ø–µ—Ä–≤–µ—Å—Ç–∏ —Ñ–æ—Ç–æ–≥–∞—Ä—Ñ–∏—é –≤ –Ω–µ–≥–∞—Ç–∏–≤\n/t, /text -- –¥–æ–±–∞–≤–∏—Ç—å —Ç–µ–∫—Å—Ç –∫ —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–∏\n/tc, /tester_check -- –ø—Ä–æ–≤–µ—Ä–∏—Ç—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –Ω–∞ —Ç–µ—Å—Ç–µ—Ä–∞\n/userid, /uid -- –ø–æ–ª—É—á–∏—Ç—å ID –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è\n\nüê∫ –†–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫: [id498281739|—Ç—ã–∫]",
            reply_to=message['id']
        )

    return
